module Advent
  @instructions = ["", "", "", "", ""]

  @x = 0
  @y = 0

  @instructions.each_with_index {|instruction, index|
    instruction.split(//).each {|letter|
      if letter == "L"
        @x -= 1 unless @x < 0
      elsif letter == "R"
        @x += 1 unless @x > 0
      elsif letter == "D"
        @y -= 1 unless @y < 0
      elsif letter == "U"
        @y += 1 unless @y > 0
      end
    }
    puts "instruction #{index} is #{@x}, #{@y}"
  }

end
