module Advent
  @instructions = ["", "", "", "", ""]

  @x = -2
  @y = 0

  @instructions.each_with_index {|instruction, index|
    instruction.split(//).each {|letter|
      if letter == "L"
        if @x > 0
          @x -= 1
        elsif @x == 0 && @y.abs < 2
          @x -= 1
        elsif @x == -1 && @y == 0
          @x -= 1
        end
      elsif letter == "R"
        if @x < 0
          @x += 1
        elsif @x == 0 && @y.abs < 2
          @x += 1
        elsif @x == 1 && @y == 0
          @x += 1
        end
      elsif letter == "D"
        if @y > 0
          @y -= 1
        elsif @y == 0 && @x.abs < 2
          @y -= 1
        elsif @y == -1 && @x == 0
          @y -= 1
        end
      elsif letter == "U"
        if @y < 0
          @y += 1
        elsif @y == 0 && @x.abs < 2
          @y += 1
        elsif @y == 1 && @x == 0
          @y += 1
        end
      end
    }
    puts "instruction #{index} is #{@x}, #{@y}"
  }

end
